/*
 USAGE (example: line chart)
 1. c+p this template to a new file (line.js)
 2. change puddingChartName to puddingChartLine
 3. in graphic file: import './pudding-chart/line'
 4a. const charts = d3.selectAll('.thing').data(data).puddingChartLine();
 4b. const chart = d3.select('.thing').datum(datum).puddingChartLine();
*/

d3.selection.prototype.exportsByState = function init(options) {
	function createChart(el) {
		const $sel = d3.select(el);
		let data = $sel.datum();
		// dimension stuff
		const width = 0;
		const height = 0;
		const marginTop = 0;
		const marginBottom = 0;
		const marginLeft = 0;
		const marginRight = 0;

		// scales
		const scaleX = null;
		const scaleY = null;

		// dom elements
		const $svg = null;
		const $axis = null;
		const $vis = null;
		const $containerMini = null

		// helper functions

		const Chart = {
			// called once at start
			init() {


				Chart.resize();
				Chart.render();
			},
			// on resize, update new dimensions
			resize() {
				// defaults to grabbing dimensions from container element
				// width = $sel.node().offsetWidth - marginLeft - marginRight;
				// height = $sel.node().offsetHeight - marginTop - marginBottom;
				// $svg
				// 	.attr('width', width + marginLeft + marginRight)
				// 	.attr('height', height + marginTop + marginBottom);
				return Chart;
			},
			// update scales and render chart
			render() {
				const $state = $sel.selectAll('.state')
					.data(data)
					.join(
						enter => {
							const state = enter
								.append('div')
								.attr('class', 'state')

							const $title = state.append('p')
								.attr('class', 'state-name')
								.text(d => d.key)

							const $container = state.append('div')
								.attr('class', 'container-mini')

							return state
							// .enter()
							// .append('div')
							// .attr('class', 'dog')
							//
						}
					);

				console.log({$state})

				$state.select('.container-mini').selectAll('.dog')
					.data(d => d.value.breedMap)
					.join(enter => {
						const dog = enter.append('div')
							.attr('class', 'dog')

						return dog
					})
					.style('background-image', d => `url(assets/images/profiles/${d.key}.png)`)


				//
				// const sorted = data.values
				// 	.sort((a, b) => d3.ascending(a.size, b.size))
				// 	.sort((a,b) => {
				// 		return d3.ascending(a.file, b.file)
				// 	})

				// $containerMini.selectAll('.dog')
				// 	.data(sorted)
				// 	.join(
				// 		enter => {
				// 			enter.append('div')
				// 				.attr('class', 'dog')
				// 				.style('background-image', d => `url(assets/images/profiles/${d.file}.png)`)
				// 		},
				// 		exit => {
				// 			exit.remove()
				// 		}
				// 	)



				return Chart;
			},
			// get / set data
			data(val) {
				if (!arguments.length) return data;
				data = val;
				$sel.datum(data);
				Chart.render();
				return Chart;
			}
		};
		Chart.init();

		return Chart;
	}

	// create charts
	const charts = this.nodes().map(createChart);
	return charts.length > 1 ? charts : charts.pop();
};
